#include <iostream>//Esercizio: Modellazione di una Bottiglietta d'Acqua in C++using namespace std;class BottigliettaAcqua {private:	int capienza, acquaContenuta;public:	BottigliettaAcqua(int max) {		capienza = max;		acquaContenuta = 0;	}	void riempiBottiglietta(int quantita) {		if (quantita > capienza - acquaContenuta) {			cout << "Errore: La quantità d'acqua da aggiungere supera la capacità massima della bottiglietta. Impossibile aggiungere altra acqua.\n";		}else {			acquaContenuta += quantita;		}	}	void beviAcqua(int quantita) {		if (quantita > acquaContenuta) {			cout << "Errore: La quantità d'acqua desiderata e' maggiore della quantità presente nella bottiglietta. Impossibile bere altra di quanto c'e'.\n";		}		else {			acquaContenuta -= quantita;		}	}	int getQuantitaAcqua() const {		return acquaContenuta;	}};int main(){	BottigliettaAcqua b1(2000);	cout << "La bottiglia contiene " << b1.getQuantitaAcqua() << "ml di acqua\n";	b1.beviAcqua(200);	cout << "La bottiglia contiene " << b1.getQuantitaAcqua() << "ml di acqua\n";	b1.riempiBottiglietta(2100);	cout << "La bottiglia contiene " << b1.getQuantitaAcqua() << "ml di acqua\n";	b1.riempiBottiglietta(200);	cout << "La bottiglia contiene " << b1.getQuantitaAcqua() << "ml di acqua\n";	b1.beviAcqua(50);	cout << "La bottiglia contiene " << b1.getQuantitaAcqua() << "ml di acqua\n";	return 0;}